mapper.select:
select a.account_phone,a.user_id, a.account, a.im_id, b.nickname, b.user_icon from hx_user a,hx_user_info b
where a.user_id = b.user_id and a.account_phone in
#{phone}


	SELECT 
		a.user_id, 
		a.im_id, 
		a.user_icon, 
		a.user_sex, 
		a.nickname, 
		a.user_autograph, 
		a.last_longitude, 
		a.last_latitude,  
	ROUND(
		6378.138 * 2 * ASIN(
			SQRT(
				POW(
					SIN(
						(
							#{longitude} * PI() / 180 - last_longitude * PI() / 180
						) / 2
					),
					2
				) + COS(#{longitude} * PI() / 180) * COS(last_longitude * PI() / 180) * POW(
					SIN(
						(
							#{latitude} * PI() / 180 - last_latitude * PI() / 180
						) / 2
					),
					2
				)
			)
		) * 1000
	) AS `distance` 
FROM
	hx_user_info a 
WHERE
	1 = 1 
AND a.last_longitude != "" 
AND a.last_latitude != "" 
AND a.user_id != #{user_id} 
AND a.user_id NOT IN (
	SELECT
		b.friend_id from hx_user_friend b where b.user_id = #{user_id} and b.friend_state = 1 
)

	 		and user_sex = #{user_sex}  


	 		and user_birthday  <=  #{user_birthday_min} 


	 		and user_birthday  >  #{user_birthday_max} 


	 		and user_emotion = #{user_emotion} 

ORDER BY
	`distance` ASC
LIMIT 100
