mapper.insert:
insert into hx_user_video_like (user_id, video_id, create_time
  )
values (#{user_id,jdbcType=BIGINT}, #{video_id,jdbcType=BIGINT}, #{create_time,jdbcType=BIGINT}
  )

mapper.update:
update hx_user_video_like


    create_time = #{create_time,jdbcType=BIGINT},


where user_id = #{user_id,jdbcType=BIGINT}
  and video_id = #{video_id,jdbcType=BIGINT}

update hx_user_video_like
set create_time = #{create_time,jdbcType=BIGINT}
where user_id = #{user_id,jdbcType=BIGINT}
  and video_id = #{video_id,jdbcType=BIGINT}

mapper.select:
select a.video_id, a.user_id, a.attribute_id, a.`dec`, a.content,a.image, a.create_time, a.good_num, a.look_num, a.comment_num, a.longitude, a.latitude, a.city, a.area_code, b.user_icon as user_icon, b.account as user_account , d.follow_user_id as video_follow_user_id, c.create_time as like_time
from hx_user_info b join hx_user_video a join hx_user_video_like c
left join hx_user_video_follow d on d.user_id = #{user_id} and d.follow_user_id = a.user_id where 1 = 1 and b.user_id = a.user_id and a.video_id = c.video_id and c.user_id = #{user_id}
and c.create_time < #{last_like_time}
order by c.create_time desc
limit 0,#{pageSize}
